            var mockConsumer = new Mock<IConsumer<string, string>>();
            mockConsumer.Setup(c => c.Consume(It.IsAny<TimeSpan>())).Throws(new OperationCanceledException());

            var consumerWrapper = new ConsumerWrapper(mockConfig, "test-topic");

            // Act
