            var topicName = "test-topic";

            var producerWrapper = new ProducerWrapper(config, topicName);

            // Act
            producerWrapper.Dispose();

            // Assert - no exception means successful disposal
        }

        [Fact]
        public void MultipleDispose_ShouldNotThrowException()
        {
